export default {
  name: "shapankeshihua",
  data() {
    return {
      l1: 1,
      l2: 1,
      l3: 1,
      l4: 1
    }
  },
  methods: {
    addHour: function () {
      hour += 1
      hour %= 24
      div1()
      l2()
    },
    fType1: function () {
      type1 = selectTxt('type1')
      div2()
      div5()
    },
    fAirType: function () {
      airType = selectTxt('airType')
      div2()
      div6()
      l2()
    },
    fCarType: function () {
      carType = selectTxt('carType')
      div8_1()
      l1()
    },
    fl1: function () {
      if (this.l1 === 1) {
        layer1.hide()
        this.l1 = 0
      } else {
        layer1.show()
        this.l1 = 1
      }
    },
    fl2: function () {
      if (this.l2 === 1) {
        layer2.hide()
        this.l2 = 0
      } else {
        layer2.show()
        this.l2 = 1
      }
    }
  },
  mounted() {

    addListOption('type1', ['AQI', '降水量', '风速', '气温'])
    addListOption('airType', ['CO', 'PM2.5', 'PM10', 'NO2', 'SO2', 'O3'])
    addListOption('carType', ['小型汽车', '大型汽车', '小型普通客车', '重型栏板货车', '其它', '普通二轮摩托车', '微型栏板货车', '大型普通客车', '中型栏板货车', '中型轿车', '轻型栏板货车', '大型轿车', '重型栏板半挂车', '中型普通客车'])

    div1()
    div2()
    div3()
    div4()
    div5()
    div6()
    div7()
    div8_1()


    scene.on('loaded', () => {
      fetch(localhost + year + "/" + month + "/" + day + "/" + shi + "/" + carType + "/div4_1")
          .then(res => res.json())
          .then(data => {
            layer1 = new PointLayer({})
                .source(data, {
                  parser: {
                    x: 'jd',
                    y: 'wd',
                    type: 'json'
                  }
                })
                .shape('circle')
                .size('value', [1, 32])
                .color('value', [
                  '#8C1EB2',
                  '#8C1EB2',
                  '#DA05AA',
                  '#F0051A',
                  '#FF2A3C',
                  '#FF4818',
                  '#FF4818',
                  '#FF8B18',
                  '#F77B00',
                  '#ED9909',
                  '#ECC357',
                  '#EDE59C'
                ])
                .active(true)
                .animate({})
                .style({
                  opacity: 1,
                  strokeWidth: 0
                });
            scene.addLayer(layer1);
            layer1.on('click', function (ev) {
              kakou = ev['feature']['name']
              div8_1()
            })
          });
    });

    scene.on('loaded', () => {
      fetch(localhost + year + "/" + month + "/" + day + "/" + hour + "/" + airType + "/div4_2")
